import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./../css/style.css"

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [movies, setMovies] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [details, setDetails] = useState([]);

  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = (user) => {
    setModalIsOpen(true);
    setCurrentUser(user);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  // Funkcja do dodawania filmów do biblioteki użytkownika - dostosuj ją do Twojej logiki
  const addMovieToLibrary = (movie) => {
    // Implementacja logiki dodawania filmu do biblioteki użytkownika
    // Prawdopodobnie będziesz musiał wysłać odpowiednie zapytanie do API
    // np. axios.post('/api/v1/addMovieToLibrary', { userId: currentUser.id, movieId: movie.id });
    // Następnie zaktualizuj stan lub wykonaj odpowiednie akcje po dodaniu filmu
  };


  useEffect(() => {
    axios.get('/api/v1/users').then(response => {
      setUsers(response.data);
    });

    axios.get('/api/v1/allMovies').then(response => {
      setMovies(response.data);
    });
  }, []);

  const handleUserClick = user => {
    // Pobierz filmy obejrzane przez użytkownika
    axios.get(`/api/v1/userMovies?userId=${user.id}`).then(response => {
      setDetails(response.data);
      setSelectedMovie(null);
    });
    setSelectedUser(user);
  };

  const handleMovieClick = movie => {
    // Pobierz użytkowników, którzy obejrzeli film
    alert(movie.name)
    axios.get(`/api/v1/users/movies?movieId=${movie.id}`).then(response => {
      setDetails(response.data);
      setSelectedUser(null);
    });
    setSelectedMovie(movie);
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <div>
          <h2>Użytkownicy</h2>
          <ul>
            {users.map(user => (
              <li key={user.id} onClick={() => handleUserClick(user)}>
                {user.firstname + " " + user.lastname + " (" + user.email + ")"}
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h2>Filmy</h2>
          <ul>
            {movies.map(movie => (
              <li key={movie.id} onClick={() => handleMovieClick(movie)}>
                {movie.name}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div>
        <h2>Szczegóły</h2>
        {selectedUser && <p>Filmy obejrzane przez użytkownika {selectedUser.firstname + " " + selectedUser.lastname}</p>}
        {selectedMovie && <p>Użytkownicy, którzy obejrzeli film {selectedMovie.name}</p>}
        <ul>
          {details.map(detail => (
            <li key={detail.id}>{detail.name || detail.firstname}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
