import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [movies, setMovies] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [details, setDetails] = useState([]);

  useEffect(() => {
    axios.get('/api/v1/users').then(response => {
      setUsers(response.data);
    });

    axios.get('/api/v1/allMovies').then(response => {
      setMovies(response.data);
    });
  }, []);

  const handleUserClick = userId => {
    // Pobierz filmy obejrzane przez użytkownika
    axios.get(`/api/v1/userMovies?userId=${userId}`).then(response => {
      setDetails(response.data);
      setSelectedMovie(null);
    });
    setSelectedUser(userId);
  };

  const handleMovieClick = movie => {
    // Pobierz użytkowników, którzy obejrzeli film
    alert(movie)
    axios.get(`/api/v1/users/movies?movieId=${movie.id}`).then(response => {
      setDetails(response.data);
      setSelectedUser(null);
    });
    setSelectedMovie(movie);
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <div>
          <h2>Użytkownicy</h2>
          <ul>
            {users.map(user => (
              <li key={user.id} onClick={() => handleUserClick(user.id)}>
                {user.firstname + " " + user.lastname + " (" + user.email + ")"}
              </li>
            ))}
          </ul>
        </div>
        <div>
          <h2>Filmy</h2>
          <ul>
            {movies.map(movie => (
              <li key={movie.id} onClick={() => handleMovieClick(movie)}>
                {movie.name}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div>
        <h2>Szczegóły</h2>
        {selectedUser && <p>Filmy obejrzane przez użytkownika {selectedUser}</p>}
        {selectedMovie && <p>Użytkownicy, którzy obejrzeli film {selectedMovie.name}</p>}
        <ul>
          {details.map(detail => (
            <li key={detail.id}>{detail.name || detail.firstname}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Dashboard;
