import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';
import "./../css/style.css"

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [movies, setMovies] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [details, setDetails] = useState([]);

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);


  const openModal = (user) => {
    setModalIsOpen(true);
    setCurrentUser(user);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const addMovieToLibrary = (movie) => {
    axios.post(
        "/api/v1/users/movies", 
        {
            userId: currentUser.id, 
            movieId: movie.id
        }
    ).then(response => {
        alert("Movie has been added successfully");
        setModalIsOpen(false);
    }).catch(error => {
        alert("Movie has already been added");
    })
  };

  useEffect(() => {
    axios.get('/api/v1/users').then(response => {
      setUsers(response.data);
    });

    axios.get('/api/v1/allMovies').then(response => {
      setMovies(response.data);
    });
  }, []);

  const handleUserClick = user => {
    axios.get(`/api/v1/userMovies?userId=${user.id}`).then(response => {
      setDetails(response.data);
      setSelectedMovie(null);
    });
    setSelectedUser(user);
  };

  const handleMovieClick = movie => {
    axios.get(`/api/v1/users/movies?movieId=${movie.id}`).then(response => {
      setDetails(response.data);
      setSelectedUser(null);
    });
    setSelectedMovie(movie);
  };

  const handleDeleteMovieClick = movieId => {
    axios.delete(
      `/api/v1/users/movies?userId=${selectedUser.id}&movieId=${movieId}`
  ).then(response => {
      alert("Movie has been deleted successfully");
      setModalIsOpen(false);
  })
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <div>
          <h2>Users</h2>
          <ul>
            {users.map(user => (
            <li key={user.id} onClick={() => handleUserClick(user)}>
              {user.firstname + " " + user.lastname + " (" + user.email + ")"}
              <button onClick={() => openModal(user)}>+</button>
            </li>
          ))}
          </ul>
        </div>
        <div>
          <h2>Movies</h2>
          <ul>
            {movies.map(movie => (
              <li key={movie.id} onClick={() => handleMovieClick(movie)}>
                {movie.name}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div>
        <h2>Details</h2>
        {selectedUser && <p>Movies watched by: <b>{selectedUser.firstname + " " + selectedUser.lastname}</b></p>}
        {selectedMovie && <p>Users, that watched movie: <b>{selectedMovie.name}</b></p>}
        <ul>
        {details.map(detail => (
           <li key={detail.id}>
            {detail.name + " " || detail.firstname}
            {detail.name && (
              <button onClick={() => handleDeleteMovieClick(detail.id)}>-</button>
            )}
          </li>
        ))}

        </ul>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        style={{
            content: {
                width: '50%',
                margin: 'auto'
            }}
        }
      >
        <h2>Add movie for user {currentUser && currentUser.firstname + " " + currentUser.lastname}</h2>
        <ul>
          {movies.map(movie => (
            <li key={movie.id}>
              {movie.name}
              <button onClick={() => addMovieToLibrary(movie)} style={{float: "right"}}>Add</button>
            </li>
          ))}
        </ul>
        <button onClick={closeModal}>Close</button>
      </Modal>
    </div>
  );
};

export default Dashboard;
