import React, { useState, useEffect } from 'react';
import Modal from 'react-modal';
import axios, { AxiosError } from 'axios';
import "./../css/style.css"

const Dashboard = () => {
  const [users, setUsers] = useState([]);
  const [movies, setMovies] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [details, setDetails] = useState([]);

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);


  const openModal = (user) => {
    setModalIsOpen(true);
    setCurrentUser(user);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const addMovieToLibrary = (movie) => {
    axios.post(
        "/api/v1/users/movies", 
        {
            userId: currentUser.id, 
            movieId: movie.id
        }
    ).catch(function (error) {
        alert(error.headers)
    })
  };

  useEffect(() => {
    axios.get('/api/v1/users').then(response => {
      setUsers(response.data);
    });

    axios.get('/api/v1/allMovies').then(response => {
      setMovies(response.data);
    });
  }, []);

  const handleUserClick = user => {
    axios.get(`/api/v1/userMovies?userId=${user.id}`).then(response => {
      setDetails(response.data);
      setSelectedMovie(null);
    });
    setSelectedUser(user);
  };

  const handleMovieClick = movie => {
    axios.get(`/api/v1/users/movies?movieId=${movie.id}`).then(response => {
      setDetails(response.data);
      setSelectedUser(null);
    });
    setSelectedMovie(movie);
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-around' }}>
        <div>
          <h2>Użytkownicy</h2>
          <ul>
            {users.map(user => (
            <li key={user.id} onClick={() => handleUserClick(user)}>
              {user.firstname + " " + user.lastname + " (" + user.email + ")"}
              <button onClick={() => openModal(user)}>+</button>
            </li>
          ))}
          </ul>
        </div>
        <div>
          <h2>Filmy</h2>
          <ul>
            {movies.map(movie => (
              <li key={movie.id} onClick={() => handleMovieClick(movie)}>
                {movie.name}
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div>
        <h2>Szczegóły</h2>
        {selectedUser && <p>Filmy obejrzane przez użytkownika {selectedUser.firstname + " " + selectedUser.lastname}</p>}
        {selectedMovie && <p>Użytkownicy, którzy obejrzeli film {selectedMovie.name}</p>}
        <ul>
          {details.map(detail => (
            <li key={detail.id}>{detail.name || detail.firstname}</li>
          ))}
        </ul>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        // dodatkowe opcje stylowania modalnego okna
      >
        <h2>Dodaj Film dla {currentUser && currentUser.firstname}</h2>
        <ul>
          {movies.map(movie => (
            <li key={movie.id}>
              {movie.name}
              <button onClick={() => addMovieToLibrary(movie)}>Dodaj</button>
            </li>
          ))}
        </ul>
        <button onClick={closeModal}>Zamknij</button>
      </Modal>
    </div>
  );
};

export default Dashboard;
